import { useEffect } from "react";
import { getAllProducts } from "../../server";
import { ProductCard } from "../../ui";
import { getAllProduct } from "../../Redux/Products/productSlice";
import { dataProductSlice } from "../../Redux/Products/productSlice";
import { useSelector, useDispatch } from "react-redux";
import { IProduct } from "../../type";
import { useLocation } from 'react-router-dom';

import './style/index.scss'

const Product = () => {

  const dispatch = useDispatch();
  const { items } = useSelector(dataProductSlice);
  const location = useLocation()

  useEffect(() => {
    getAllProducts(location?.pathname?.split('/')[1]).then((res) => dispatch(getAllProduct(res ?? [])));
  }, [dispatch, location?.pathname]);

  return (
    <div className="batman-store__container">
      {items?.map((item: IProduct) => (
        <ProductCard {...item} />
      ))}
    </div>
  );
};

export default Product;
