import {NavigationItem} from '../index'
import './stytle.scss'
import {useState} from "react";
import {INavigationType} from '../../type'
import {dataNavigation} from "../../server";
// import HomeIco from '../img/home.svg'


interface  currentNavigationType {
    arr : INavigationType[]
}

const NavigationList = () => {


    const handleActive = (id:string) =>{
        data?.map(item => {
            if(item?.id === id){
                setCurrentId(id)
            }
        })
    }
const arr = [{id:'transport', value:'Transport', img:'', active:true},
    {id:'real_estate', value:'Real Estate', img:'', active:false, onClick:{handleActive}},
    {id:'technics', value:'Technics', img:'', active:false, onClick:{handleActive}},
    {id:'cloth', value:'Cloth', img:'', active:false, onClick:{handleActive}},
    {id:'appliances', value:'Appliances', img:'', active:false, onClick:{handleActive}},]

    const [data, setData] = useState<INavigationType[]>(currentNavigationType)
    const [currentId, setCurrentId] = useState('transport')

    if (currentId) {
      data?.map(item => {
          return  item?.id === currentId ? item.active = true : item.active = false
      })

    }

  return(
      <div className="batman-store__header-container_navigation">
          <ul className='batman-store__header-container_navigation-list'>

              {data?.map((item:INavigationType) => <NavigationItem key={item?.id}   item={item}/>)}

          </ul>
      </div>
  )
}

export default NavigationList
