const { MongoClient, Collection } = require('mongodb');

const express = require('express');


export const dataNavigation = [
  { id: ' ', value: 'Home', img: '' },
  { id: 'transport', value: 'Transport', img: '' },
  { id: 'real_estate', value: 'Real Estate', img: '' },
  { id: 'technics', value: 'Technics', img: '' },
  { id: 'cloth', value: 'Cloth', img: '' },
  { id: 'appliances', value: 'Appliances', img: '' },
]


interface User {
  name: string;
  age: number;
  email: string;
}

const uri = 'mongodb+srv://Cavliuc:Qwerty123@cardproduct.3rbu7rh.mongodb.net/?retryWrites=true&w=majority';

const app = express();

// Retrieve all users
app.get('/users', async (req:any, res:any) => {
  const client: MongoClient = new MongoClient(uri, {
    //@ts-ignore
    useNewUrlParser: true,
    useUnifiedTopology: true
  });

  try {
    await client.connect();
    console.log('Connected to MongoDB');

    const db = client.db('test');
    const users: Collection<User> = db.collection('users');

    const result: User[] = await users.find({}).toArray();
console.log(res)
    res.send(result);
  } catch (err) {
    console.error(err);
    res.status(500).send('Internal server error');
  } finally {
    await client.close();
    console.log('Disconnected from MongoDB');
  }
});

app.listen(3000, () => {
  console.log('Server listening on port 3000');
});
