import "./style/index.scss";
import { IProduct } from "../../type";

const ProductCard = ({
  brand,
  category,
  description,
  discountPercentage,
  id,
  images,
  price,
  rating,
  stock,
  thumbnail,
  title,
}: IProduct) => {
  return (
    <div key={id} className="batman-ui__card">
      <div className="batman-ui__card_image">
        <img src={images} alt="imageProduct" />
      </div>
      <div className="batman-ui__card_info">
        <p>{brand}</p>
        <p>{title}</p>
      </div>
      <div className="batman-ui__card_price">
        <p>Price: {price}</p>
      </div> <div className="batman-ui__card_more-info">
            <p>{description}</p>
      </div>
    </div>
  );
};

export default ProductCard;
