export const x_csrf_token =
  document?.getElementById("X-CSRF-TOKEN")?.value ?? "";

export const getReducerType = (type = "", key = "", after = "") => {
  return (
    type.toUpperCase() +
    "_" +
    key.toUpperCase() +
    (after ? "_" + after.toUpperCase() : "")
  );
};

export const getActionValue = (type, key, payload) => {
  return { type: getReducerType(type, key), payload };
};

export const getActionSelect = (type, key) => {
  const reducer_type = (v) => getReducerType(type, key, v);

  return {
    selected: (payload) => ({ type: reducer_type("selected"), payload }),
    items: (payload) => ({ type: reducer_type("items"), payload }),
    loading: (payload) => ({ type: reducer_type("loading"), payload }),
  };
};

export const getValidate = (data = []) => {
  const res = {};

  data.forEach((item) => {
    let isEmpty = false;
    let message = [];
    let error = false;

    if (item?.type === "select") {
      isEmpty = !item?.value?.id;
      error = isEmpty;
    }

    if (item?.type === "input") {
      isEmpty = !item?.value?.trim();
      error = isEmpty;
    }

    if (!isEmpty) {
      const validations = item?.validation ?? [];

      validations.forEach((val) => {
        if (val?.condition) {
          error = true;
          message.push(val?.message ?? "");
        }
      });
    }

    message = message
      .map(
        (err, i) =>
          (i ? err[0].toLowerCase() : err[0].toUpperCase()) + err.slice(1)
      )
      .join(", ");

    if (item?.id) {
      res[item.id] = { error, message };
    }
  });

  return res;
};
