    const [data, setData] = useState<dataNavigation[]>(curentNavigation)
    const [currentId, setCurrentId] = useState('transport')

    if (currentId) {
      data?.map(item => {
          return  item?.id === currentId ? item.active = true : item.active = false
      })

    }

  return(
      <div className="batman-store__header-container_navigation">
          <ul className='batman-store__header-container_navigation-list'>

              {data?.map((item:INavigationType) => <NavigationItem key={item?.id}   item={item}/>)}

          </ul>
      </div>
  )
}

export default NavigationList
