import { getReducerType } from "../../utils";

const getType = (key, after) => getReducerType("products", key, after);

const initialState = {
  products: {
    items: [],
  },
};

const car = (state = initialState, action) => {
  const { type, payload } = action;

  switch (type) {
    case getType("products"): {
      return {
        ...state,
        products: {
          ...state.products,
          items: payload,
        },
      };
    }
    default: {
      return state;
    }
  }
};

export default car;
