import { getReducerType } from "../../utils";

const getType = (key, after) => getReducerType("products", key, after);

const initialState = {
  brand:"",
  category:"",
  description:"",
  discountPercentage:null,
  id:null,
  images:"",
  price:null,
  rating:null,
  stock:null,
  thumbnail:"",
  title: "",
};

const car = (state = initialState, action) => {
  const { type, payload } = action;

  switch (type) {
    case getType("products"): {
      return {
        ...state,
        products: payload,
      };
    }
    default: {
      return state;
    }
  }
};

export default car;
