import { MongoClient, Collection, Cursor, Document } from 'mongodb';



export const dataNavigation = [
  { id: ' ', value: 'Home', img: '' },
  { id: 'transport', value: 'Transport', img: '' },
  { id: 'real_estate', value: 'Real Estate', img: '' },
  { id: 'technics', value: 'Technics', img: '' },
  { id: 'cloth', value: 'Cloth', img: '' },
  { id: 'appliances', value: 'Appliances', img: '' },
]

export const getAllProducts = () => {
  return fetch('https://dummyjson.com/products')
    .then((res) => res.json())
    .then((body) => body.products)
}

interface User {
  name: string;
  age: number;
  email: string;
}

const uri = 'mongodb+srv://<username>:<password>@<cluster>/<database>?retryWrites=true&w=majority';

async function main(): Promise<void> {
  const client: MongoClient = new MongoClient(uri, {
    useNewUrlParser: true,
    useUnifiedTopology: true
  });

  try {
    await client.connect();
    console.log('Connected to MongoDB');

    const db = client.db('test');
    const users: Collection<User> = db.collection('users');

    // Retrieve all documents from the collection
    const allUsers: Cursor<Document<User>> = users.find({});
    await allUsers.forEach((user: Document<User>) => console.log(user));

    // Retrieve documents where age is greater than 18
    const adults: Cursor<Document<User>> = users.find({ age: { $gt: 18 } });
    await adults.forEach((user: Document<User>) => console.log(user));
  } catch (err) {
    console.error(err);
  } finally {
    await client.close();
    console.log('Disconnected from MongoDB');
  }
}

main();
